{
  ctx.fillStyle    = (bDisabled) ? "#f0f0f0" : sFontColor;
  ctx.textBaseline = "middle";
  ctx.textAlign    = "center";
  ctx.font         = nFontSize + "px Arial";

  if(nPush)
    ctx.transform(1, 0, 0, 1, nBorder, nBorder);

  ctx.fillText(sTitle , dx * 0.5, dy * 0.5);
}

