  ctx.lineTo(dx * 0.45, dy * 0.2);
  ctx.lineTo(dx * 0.45, dy * 0.8);
  ctx.lineTo(dx * 0.3, dy * 0.8);
  ctx.closePath();
  ctx.fill();

