  ctx.lineTo(dx * 0.7, dy * 0.2);
  ctx.lineTo(dx * 0.7, dy * 0.8);
  ctx.lineTo(dx * 0.55, dy * 0.8);
  ctx.closePath();
  ctx.fill();
}
else
{
  ctx.beginPath();
