  if(!pEvent.Action) //if key UP
    pMe.Methods.onPressUp();
}
pMe.Refresh();

                pMe.Vars("push").Value = pEvent.Action;
if(!pEvent.Action) //if mouse button UP
  pMe.Methods.onPressUp();
