  var nDay = tRes >= 1 ? Pm.Round(tRes, 1) : 0;
  var sDay = nDay > 0 ? nDay + " " : "";
  pMe.Vars("infozoom").Value = sDay + Pm.CreatePmDateObject(tRes - nDay).Format("%H:%M:%S");
}

