var travelCCW = pMe.Vars("Cmd1IntCCW").Value && !pMe.Vars("RunCCW").Value;
var travelStop = pMe.Vars("Cmd0Int").Value && (pMe.Vars("RunCW").Value || pMe.Vars("RunCCW").Value);

