ctx.lineTo(dx * 0.8, dy * 0.7);
ctx.lineTo(dx * 0.4, dy * 0.3);
ctx.closePath();
ctx.fill();

ctx.beginPath();
ctx.arc(dx * 0.3, dy * 0.3, dx * 0.18, 1.55 * Pm.PI, 0.95 * Pm.PI);
ctx.fill();

ctx.fillStyle = sBgColor;
ctx.beginPath();
ctx.arc(dx * 0.25, dy * 0.25, dx * 0.1, 1.55 * Pm.PI, 0.95 * Pm.PI);
ctx.fill();

// disabled fill
